    // create and return a StorageResource containing the genes found
    public int findStopCodon(String dna,int startIndex,String Codon)  
    //This method returns the index of the first occurrence of stopCodon 
    //that appears past startIndex and is a multiple of 3 away from startIndex
    { //startIndex---epresents where the first occurrence of ATG occurs in dna,
        int currIndex=dna.indexOf(Codon,startIndex+3);
        while (currIndex!=-1){
            if((currIndex-startIndex)%3==0){
                return currIndex;
            }else{
                currIndex=dna.indexOf(Codon,currIndex+1);
            }
            
        }return -1;

    }

    public String findGene(String dna,int where){
        int startIndex=dna.indexOf("ATG",where);
        //System.out.println(startIndex+"startIndex"+where+"where");
        if (startIndex==-1){
            return "";
        }//Find the index of the first occurrence of the start codon “ATG”. If there is no “ATG”, return the empty string.
        int taaIndex=findStopCodon(dna,startIndex,"TAA");
        int tagIndex=findStopCodon(dna,startIndex,"TAG");
        int tgaIndex=findStopCodon(dna,startIndex,"TGA");
        int minIndex=0;
        //System.out.println(taaIndex+"taa"+tagIndex+"tag"+tgaIndex);
        if (taaIndex==-1||(tgaIndex!=-1 && tgaIndex<taaIndex))
        {
            minIndex=tgaIndex;
        }else
        {
            minIndex=taaIndex;
        }
        if (minIndex==-1||(tagIndex!=-1 && tagIndex<minIndex))
        {
            minIndex=tagIndex;
        }
        if (minIndex==-1){
            return "";
        }
        return dna.substring(startIndex,minIndex+3);}
    
    //2.2.3 StorageResource Class
    public StorageResource getAllGenes(String dna){
        //In this method you should repeatedly find genes and print each one until there are no more genes
        StorageResource geneList=new StorageResource();
        int startIndex=0;
        while (true)
        {
            String gene=findGene(dna,startIndex);
            //System.out.println("1"+gene);
            //System.out.println(gene);
            if(gene.isEmpty()){
                break;
            }
            geneList.add(gene);;
            startIndex=dna.indexOf("ATG",startIndex)+gene.length();
        }return geneList;
    }
    
    public double cgRatio(String str1){
        char someChar = 'C';
        char someChar1 = 'G';
        double count = 0.0;  
        double ratio=0.0;
        for (int i = 0; i < str1.length(); i++) {
            if (str1.charAt(i) == someChar||str1.charAt(i) ==someChar1) {
                count++;
            }
        }
        ratio=count/str1.length();
        return ratio;}
        
    public void processGenes (StorageResource sr){
        int count=0;
        int countcg=0;
        int longest=0;
        for (String s:sr.data()){
            //System.out.println(s+"is");
            if (s.length()>3){
                System.out.println(s+"is longer than 9 characters");
                count++;
            }
            if (cgRatio(s)>0.35){
                System.out.println(s+"C-G-ratio is higher than 0.35");
                countcg++;
            }
            if (s.length()>longest){
                longest=s.length();
            }
        }
        System.out.println("The number of strings longer than 3 characters:"+count);
        System.out.println("The number of strings C-G-ratio is higher than 0.35:"+countcg);
        System.out.println("the length of the longest gene in sr:"+longest);
    }
    
